import React, { Component } from "react";
import { ListGroup, Button } from "react-bootstrap";
import axios from "axios";

export default class Pokemones extends Component {
  state = {
    favoritos: [],
    pokemones: [],
  };
  async componentDidMount() {
    const resFavoritos = await axios.get(
      process.env.REACT_APP_HOSTNAME +
        "/pokemones/user/" +
        localStorage.getItem("user_id")
    );
    const resPokemons = await axios.get("https://pokeapi.co/api/v2/pokemon/");
    this.setState({
      favoritos: resFavoritos.data,
      pokemones: resPokemons.data,
    });
    console.log(this.state.pokemones)
  }
