import React, { Component } from "react";
import axios from "axios";

export default class GetPokemon extends Component {
  state = {
    name: '',
    img: '',
    tipo: ''
  };

  async componentDidMount(){
    const res = await axios.get(this.props.url);
    this.setState({pokemon: res.data})
    console.log(this.state.pokemon.types[0].type.name)
  }

  getPokemonImgFromApi = (data) => {
    return data.sprites.other.home.fron_default;
  };

  render() {
    return (
      <div>
        <img
          src={() => this.getPokemonImgFromAp(this.state.pokemon)}
          alt="foto"
          style={{ display: "inline-block", marginRight: "40px" }}
        />
        <p style={{ display: "inline-block", marginRight: "20px" }}>
          Nombre: {this.state.pokemon.name}
        </p>
        <p style={{ display: "inline-block", marginRight: "20px" }}>
          Tipo: {this.state.pokemon.types[0].type.name}
        </p>
      </div>
    );
  }
}
