import React, { Component } from "react";
import { ListGroup } from 'react-bootstrap';
import axios from 'axios';

export default class Pokemones extends Component {
    state = {
        pokemones: []
    }
    async componentDidMount() {
        const res = await axios.get(process.env.REACT_APP_HOSTNAME + "/pokemones/user/" + localStorage.getItem("user_id"));
        this.setState({ pokemones: res.data })
    }

    eliminarPokemon = async (id) => {
        await axios.delete(process.env.REACT_APP_HOSTNAME + "/pokemones/" + id);
        window.location.reload();
    }

    render() {
        return (
            <div className="container">
                <div className="col-lg-6">
                    <ListGroup>
                        <ListGroup.Item>Cras justo odio</ListGroup.Item>
                    </ListGroup>
                </div>
                <div className="col-lg-6">
                    <ListGroup>
                        <ListGroup.Item>Cras justo odio</ListGroup.Item>
                    </ListGroup>
                </div>
            </div>
        )
    }
}
