import React, { Component } from "react";
import { ListGroup, Accordion } from "react-bootstrap";
import getPokemon from "./getPokemon";
import axios from "axios";

export default class Pokemones extends Component {
  state = {
    favoritos: [],
    pokemones: [],
  };
  async componentDidMount() {
    let pokemons = [];
    const resFavoritos = await axios.get(
      process.env.REACT_APP_HOSTNAME +
        "/pokemones/user/" +
        localStorage.getItem("user_id")
    );
    const response = await axios.get("https://pokeapi.co/api/v2/pokemon/");
    pokemons = response.data.results.map(async (pokemon) => {
      const res = await axios.get(pokemon.url);
      pokemon = res.data;
      return <li>{pokemon.id} </li>;
    });

    //resPokemons.data.results.map(async (pokemon) => {
    //    const res = await axios.get(pokemon.url);
    //    pokemon = res.data
    //    pokemons.push(pokemon)
    //})
    this.setState({
      favoritos: resFavoritos.data,
      pokemones: pokemons,
    });
  }

  eliminarPokemon = async (id) => {
    await axios.delete(process.env.REACT_APP_HOSTNAME + "/pokemones/" + id);
    window.location.reload();
  };

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-lg-6">
            <h3 style={{ textAlign: "center" }}>Favoritos</h3>
            <ListGroup>
              {this.state.favoritos.map((fav) => {
                <ListGroup.Item>
                  <img
                    src={fav.imagen}
                    alt="foto"
                    style={{ display: "inline-block", marginRight: "40px" }}
                  />
                  <p style={{ display: "inline-block", marginRight: "20px" }}>
                    Nombre: {fav.name}
                  </p>
                </ListGroup.Item>;
              })}
            </ListGroup>
          </div>
          <div className="col-lg-6">
            <h3 style={{ textAlign: "center" }}>Pokemones</h3>
            <Accordion defaultActiveKey="0">
              <Accordion.Item eventKey="0">
                <Accordion.Header>Accordion Item #1</Accordion.Header>
                <Accordion.Body>

                </Accordion.Body>
              </Accordion.Item>
              <Accordion.Item eventKey="1">
                <Accordion.Header>Accordion Item #2</Accordion.Header>
                <Accordion.Body>
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                  do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                  Ut enim ad minim veniam, quis nostrud exercitation ullamco
                  laboris nisi ut aliquip ex ea commodo consequat. Duis aute
                  irure dolor in reprehenderit in voluptate velit esse cillum
                  dolore eu fugiat nulla pariatur. Excepteur sint occaecat
                  cupidatat non proident, sunt in culpa qui officia deserunt
                  mollit anim id est laborum.
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
          </div>
        </div>
      </div>
    );
  }
}
