import React, { Component } from "react";
import { ListGroup, Button } from "react-bootstrap";
import axios from "axios";

export default class Pokemones extends Component {
  state = {
    favoritos: [],
    pokemones: []
  };
  async componentDidMount() {
    const res = await axios.get(
      process.env.REACT_APP_HOSTNAME +
        "/pokemones/user/" +
        localStorage.getItem("user_id")
    );
    this.setState({ favoritos: res.data });
  }
  
  getPokemonsByAPI = async () =>{
    const res = await axios.get("https://pokeapi.co/api/v2/pokemon/")
  }

  eliminarPokemon = async (id) => {
    await axios.delete(process.env.REACT_APP_HOSTNAME + "/pokemones/" + id);
    window.location.reload();
  };

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-lg-6">
            <h3 style={{textAlign: "center"}}>Favoritos</h3>
            <ListGroup>
              <ListGroup.Item>
                <img alt="foto" style={{display: "inline-block", marginRight: "40px"}}/>
                <p style={{display: "inline-block", marginRight: "20px"}}>Nombre: Pikachu</p>
              </ListGroup.Item>
            </ListGroup>
          </div>
          <div className="col-lg-6">
            <h3 style={{textAlign: "center"}}>Pokemosnes</h3>
            <ListGroup>
              <ListGroup.Item>
                <img alt="foto" style={{display: "inline-block", marginRight: "40px"}}/>
                <p style={{display: "inline-block", marginRight: "20px"}}>Nombre: Pikachu</p>
                <p style={{display: "inline-block", marginRight: "20px"}}>Tipo: Rayo</p>
              </ListGroup.Item>
            </ListGroup>
          </div>
        </div>
      </div>
    );
  }
}
