import React, { Component } from "react";
import { ListGroup } from "react-bootstrap";
import axios from "axios";

export default class Pokemones extends Component {
  state = {
    favoritos: [],
    pokemones: [],
  };
  async componentDidMount() {
    let pokemons = []
    const resFavoritos = await axios.get(
      process.env.REACT_APP_HOSTNAME +
        "/pokemones/user/" +
        localStorage.getItem("user_id")
    );
    const response = await axios.get("https://pokeapi.co/api/v2/pokemon/")
    pokemons = response.data.results.map(async pokemon => {
        const res = await axios.get(pokemon.url)
        pokemon = res.data;
        <li>
            {pokemon.id}
        </li>
    });
    
    
    
    
    //resPokemons.data.results.map(async (pokemon) => {
    //    const res = await axios.get(pokemon.url);
    //    pokemon = res.data
    //    pokemons.push(pokemon)  
    //})
    this.setState({
      favoritos: resFavoritos.data,
      pokemones: pokemons,
    });
  }
  getPokemonImgFromApi = (data) =>{
    return data.sprites.other.home.fron_default;
  }

  eliminarPokemon = async (id) => {
    await axios.delete(process.env.REACT_APP_HOSTNAME + "/pokemones/" + id);
    window.location.reload();
  };

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-lg-6">
            <h3 style={{ textAlign: "center" }}>Favoritos</h3>
            <ListGroup>
              {this.state.favoritos.map((fav) => {
                <ListGroup.Item>
                  <img
                    src={fav.imagen}
                    alt="foto"
                    style={{ display: "inline-block", marginRight: "40px" }}
                  />
                  <p style={{ display: "inline-block", marginRight: "20px" }}>
                    Nombre: {fav.name}
                  </p>
                </ListGroup.Item>
              })}
            </ListGroup>
          </div>
          <div className="col-lg-6">
            <h3 style={{ textAlign: "center" }}>Pokemones</h3>
            <ul>
                {this.state.pokemones}
            </ul>
          </div>
        </div>
      </div>
    );
  }
}
