import React, { Component } from "react";
import { ListGroup, Button } from "react-bootstrap";
import axios from "axios";

export default class Pokemones extends Component {
  state = {
    favoritos: [],
    pokemones: [],
  };
  async componentDidMount() {
    let pokemons = []
    const resFavoritos = await axios.get(
      process.env.REACT_APP_HOSTNAME +
        "/pokemones/user/" +
        localStorage.getItem("user_id")
    );
    const resPokemons = await axios.get("https://pokeapi.co/api/v2/pokemon/");
    resPokemons.data.results.map(async (pokemon) => {
        const res = await axios.get(pokemon.url);
        pokemon = this.getPokemonFromApi(res.data)
        pokemons.push(pokemon)  
    })
    this.setState({
      favoritos: resFavoritos.data,
      pokemones: pokemons,
    });
    console.log(this.state.pokemones)
  }
  getPokemonImgFromApi = (data) =>{
    return data.sprites.other.home.fron_default;
  }

  getPokemonFromApi = async (pokemon) =>{
    
    return res.data
  }

  eliminarPokemon = async (id) => {
    await axios.delete(process.env.REACT_APP_HOSTNAME + "/pokemones/" + id);
    window.location.reload();
  };

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-lg-6">
            <h3 style={{ textAlign: "center" }}>Favoritos</h3>
            <ListGroup style={{ overflowY: "scroll" }}>
              {this.state.favoritos.map((fav) => {
                <ListGroup.Item>
                  <img
                    src={fav.imagen}
                    alt="foto"
                    style={{ display: "inline-block", marginRight: "40px" }}
                  />
                  <p style={{ display: "inline-block", marginRight: "20px" }}>
                    Nombre: {fav.name}
                  </p>
                </ListGroup.Item>;
              })}
            </ListGroup>
          </div>
          <div className="col-lg-6">
            <h3 style={{ textAlign: "center" }}>Pokemosnes</h3>
            <ListGroup style={{ overflowY: "scroll" }}>
              {this.state.pokemones.map((pokemon) => {
                <ListGroup.Item>
                  <img src={() => this.getPokemonImgFromAp(pokemon)} 
                    alt="foto" style={{ display: "inline-block", marginRight: "40px" }}/>
                  <p style={{ display: "inline-block", marginRight: "20px" }}> Nombre: {pokemon.name}</p>
                  <p style={{ display: "inline-block", marginRight: "20px" }}> Tipo: {pokemon.types}</p>
                </ListGroup.Item>;
              })}
            </ListGroup>
          </div>
        </div>
      </div>
    );
  }
}
